import React, { useEffect, useState } from "react";
import ApexCharts from 'apexcharts'
import { useAuth } from './AuthContext';
import { Link } from 'react-router-dom';
import "./Main.scss";

export const Main = () => {
  const { isLoggedIn, logout } = useAuth();

  const LeftSidebar = () => {
    const [isOpen, setIsOpen] = useState(false);

    const handleClick = () => {
      setIsOpen(!isOpen);
    };

    return (
      <main className="main">
        {isLoggedIn ? (
          <div className="main__content">
            <nav className="left-sidebar" id="left-sidebar">
              <ul className="left-sidebar__list">
                <li className="left-sidebar__item">
                  <a href={isOpen ? '#' : '#left-sidebar'} className="left-sidebar__link left-sidebar__link--open" onClick={handleClick}>Open</a>
                </li>
                <li className="left-sidebar__item">
                  <Link to="/filter" className="left-sidebar__link left-sidebar__link--filter">Filter</Link>
                </li>
                <li className="left-sidebar__item">
                  <Link to="/chart" className="left-sidebar__link left-sidebar__link--chart">Chart</Link>
                </li>
              </ul>
            </nav>
          </div>
        ) : (
          <h1 className="main__header">
            Вітаємо в проекті
            <br></br>
            “Планування”
          </h1>
        )}
      </main>
    );
  }

  return <LeftSidebar />;
}
