import React from "react";
import ReactDOM from "react-dom";
import ReactApexChart from "react-apexcharts";
import "./Chart1.scss"

export class Chart1 extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      series: [
        {
          name: "High - 2013",
          data: [28, 29, 33, 36, 32, 32, 33]
        },
        {
          name: "Low - 2013",
          data: [12, 11, 14, 18, 17, 13, 13]
        },
        {
          name: "High Average",
          data: this.calculateAverage([28, 29, 33, 36, 32, 32, 33]) // Calculate the high average
        },
        {
          name: "Low Average",
          data: this.calculateAverage([12, 11, 14, 18, 17, 13, 13]) // Calculate the low average
        }

      ],
      options: {
        chart: {
          type: 'line',
          dropShadow: {
            enabled: true,
            color: '#000',
            top: 18,
            left: 7,
            blur: 10,
            opacity: 0.2
          },
          stroke: {
            width: [1, 1, 1, 1], // Set the width for all lines (you can adjust as needed)
            dashArray: [0, 5, 5, 5], // Set the dash pattern for each line (0 means solid)
          },
          toolbar: {
            show: false
          }
        },
      }
    };
  }

  handleResetScope = () => {
    this.setState(prevState => ({
      options: {
        ...prevState.options, // Keep the existing options
        chart: {
          ...prevState.options.chart, // Keep the existing chart options
          type: 'line',
          dropShadow: {
            enabled: true,
            color: '#000',
            top: 18,
            left: 7,
            blur: 10,
            opacity: 0.2
          },
          stroke: {
            width: [1, 1, 1, 1],
            dashArray: [0, 5, 5, 5],
          },
          toolbar: {
            show: false
          }
        }
      }
    }));
  };

  calculateAverage(data) {
    const sum = data.reduce((total, value) => total + value, 0);
    const average = (sum / data.length).toFixed(2);

    return data.map(() => average);
  }



  render() {
    return (
      <div className="chart-container">
        <h2>Chart 1</h2>
        <div id="chart">
          <ReactApexChart
            options={this.state.options}
            series={this.state.series}
            type="line"
            height={350}
          />
          <div>
            <button onClick={this.handleResetScope}>Reset Scope</button>
          </div>
        </div>
      </div>
    );
  }
}

const domContainer = document.querySelector("#root");
ReactDOM.render(React.createElement(Chart1), domContainer);
