import React, { useEffect, useState }  from "react";
import ApexCharts from 'apexcharts'
import { useAuth } from './AuthContext';
import { Link } from 'react-router-dom';
import "./Main.scss";

export const Main = () => {
  const { isLoggedIn, logout } = useAuth();


    return (
       <main className="main">
        {isLoggedIn ? (
             <nav className="left-sidebar">
      <ul className="left-sidebar__list">
        <li className="left-sidebar__item">
          <Link to="/" className="left-sidebar__link left-sidebar__link--open">Open</Link>
        </li>
        <li className="left-sidebar__item">
          <Link to="/filter" className="left-sidebar__link left-sidebar__link--filter">Filter</Link>
        </li>
        <li className="left-sidebar__item">
          <Link to="/chart" className="left-sidebar__link left-sidebar__link--chart">Chart</Link>
        </li>
      </ul>
    </nav>
        ) : (

            <h1 className="main__header">
                Вітаємо в проекті
                <br></br>
                “Планування”
            </h1>

        )}
       </main>
    );
}
