import React from "react";
import { useTable, useFilters } from "react-table";
import someData from "../staticData.json";
import "./Filter.scss";

export const Filter = () => {
    const tableData = React.useMemo(() => someData, []);
    const columns = React.useMemo(() => [
        {
            Header: "ID Транзакції",
            accessor: "id",
        },
        {
            Header: "Дата транзакції",
            accessor: "date",
        },
        {
            Header: "Ім'я ",
            accessor: "name",
        },
        {
            Header: "Value",
            accessor: "amount",
        },
        {
            Header: "Transaction time",
            accessor: "time",
        },
    ], []);

    const {
        getTableProps,
        getTableBodyProps,
        headerGroups,
        rows,
        prepareRow,
        state,
        setFilter,
    } = useTable(
        {
            columns,
            data: tableData,
        },
        useFilters
    );

    return (
        <div className="table-container">
            <div className="filters">
                {headerGroups.map((headerGroup) => (
                    <div key={headerGroup.id} {...headerGroup.getHeaderGroupProps()} className="filter-header">
                        {headerGroup.headers.map((column) => (
                            <div key={column.id} className="filter-column">
                                <span>{column.render("Header")}</span>
                                <input
                                    value={state.filters.find((f) => f.id === column.id)?.value || ""}
                                    onChange={(e) => {
                                        setFilter(column.id, e.target.value);
                                    }}
                                />
                            </div>
                        ))}
                    </div>
                ))}
            </div>
            <table {...getTableProps()} className="full-width-table">
                <thead>
                    {headerGroups.map((headerGroup) => (
                        <tr {...headerGroup.getHeaderGroupProps()}>
                            {headerGroup.headers.map((column) => (
                                <th {...column.getHeaderProps()}>
                                    {column.render("Header")}
                                </th>
                            ))}
                        </tr>
                    ))}
                </thead>
                <tbody {...getTableBodyProps()}>
                    {rows.map(row => {
                        prepareRow(row);
                        return (
                            <tr {...row.getRowProps()}>
                                {row.cells.map(cell => {
                                    return <td {...cell.getCellProps()}>{cell.render('Cell')}</td>;
                                })}
                            </tr>
                        );
                    })}
                </tbody>
            </table>
        </div>
    );
};
